package variableclass;

public class SwapwithoutVariable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x=20;
		int y=18;
		
		x=x+y;//x=38
		y=x-y;//y=38-18/y=20
		x=x-y;//38-20=18
		
		System.out.println("x after:"+x);
		System.out.println("y after:"+y);
		
		
	}

}
